example.code:
$( "#position1" ).position({
	my: "center",
	at: "center",
	of: "#targetElement"
});

$( "#position2" ).position({
	my: "left top",
	at: "left top",
	of: "#targetElement"
});

$( "#position3" ).position({
	my: "right center",
	at: "right bottom",
	of: "#targetElement"
});

$( document ).mousemove(function( event ) {
	$( "#position4" ).position({
		my: "left+3 bottom-3",
		of: event,
		collision: "fit"
	});
});

